    private int getIndexInArray(String problem) {
        int  iter;
        if(sex.compareToIgnoreCase("female")==0)
        {
            iter = SNF.length - 1 ;
            for(; iter >0 ; iter-- )
                if((SNF[iter].compareToIgnoreCase(problem)) == 0)
                    break;
        }
        else
        {
            iter = SNM.length - 1;
            for(; iter >=0 ; iter-- )
                if((SNM[iter].compareToIgnoreCase(problem)) == 0)
                    break;
        }
        return iter;
    }

